import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  public name = "";

  constructor(private router: Router){}

  onSearch(form: NgForm){
    console.log("object", form.value.search );
    this.router.navigate(["search", form.value.search]);
    form.resetForm(); 
  }

}
<header class="shadow-md p-4">
  <form
    #form="ngForm"
    (ngSubmit)="onSearch(form)"
    class="container mx-auto flex items-center"
  >
  <div>
      <h2 class="mr-5">
        <a routerLink="/"> Customers<span>App</span> </a>
      </h2>
      <div class="flex items-center">
        <div>
          <input
            type="text"
            class="p-5 border rounded w-[400px]"
            placeholder="Search by name.."
            name="search"
            [(ngModel)]="name"
          />
        </div>
        <button class="bg-[#3f51b5] p-5 text-white rounded-tr rounded-br">
          Search
        </button>
      </div>
  </div>

  <button class="btn">
    Add Customer
  </button>

  </form>
</header>
