import { NgFor } from '@angular/common';
import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { HttpService } from 'src/app/services/http.service';

@Component({
  selector: 'app-add-customers',
  templateUrl: './add-customers.component.html',
  styleUrls: ['./add-customers.component.css']
})
export class AddCustomersComponent {

  public isLoading = false;
  public showToast = false;

  constructor(private http: HttpService, private router: Router){}

  public formData = {
    name: "",
    age: "",
    postCode: "",
    gender: "",
    street: "",
    houseNumber: "",
    order_amount: "",
  }

  addCustomer(form: NgForm){
    this.isLoading = true;
    console.log("formData", this.formData);
    this.http.post("/customers", this.formData, (data: any) => {
      if(data.success){
        this.isLoading = false;
        this.showToast = true;
        form.resetForm();
      }
    });
  }

}
<form #f="ngForm" (ngSubmit)="addCustomer(f)" class="w-[60%] mx-auto">
  <div>
    <mat-label>Name</mat-label>
    <mat-form-field>
      <input 
        matInput 
        placeholder="Isaiah" 
        ngModel
        #name="ngModel"
        required 
      />
    </mat-form-field>
  </div>

  <div>
    <mat-label>Age</mat-label>
    <mat-form-field>
      <input 
        matInput placeholder="24" [(ngModel)]="formData.age" />
    </mat-form-field>
  </div>

  <div>
    <mat-label>Gender</mat-label>
    <mat-form-field>
      <input matInput placeholder="male" [(ngModel)]="formData.gender" />
    </mat-form-field>
  </div>

  <div>
    <mat-label>Street</mat-label>
    <mat-form-field>
      <input matInput placeholder="1st main street" [(ngModel)]="formData.street" />
    </mat-form-field>
  </div>

  <div>
    <mat-label>Street</mat-label>
    <mat-form-field>
      <input matInput placeholder="273628" [(ngModel)]="formData.postCode" />
    </mat-form-field>
  </div>

  <div>
    <mat-label>Street</mat-label>
    <mat-form-field>
      <input matInput placeholder="36" [(ngModel)]="formData.houseNumber" />
    </mat-form-field>
  </div>

  <div>
    <mat-label>order amount</mat-label>
    <mat-form-field>
      <input matInput placeholder="36" [(ngModel)]="formData.order_amount" />
    </mat-form-field>
  </div>

  <div>
    <button class="btn">Submit</button>
  </div>
</form>
